  {agent_id: 4, user_id: 10, summary_question: "What costs and fees could I expect with a payday loan?", summary_answer: "Usually a fixed fee on the amount you borrow, like $15 per $100 borrowed (equal to about 400% interest).", last_message_at: "2016-10-25 15:04:02", flavor_id: 1},
  {agent_id: 4, user_id: 11, summary_question: "What is a payday loan?", summary_answer: "A short-term loan, generally for $500 or less, due on your next payday and with high interest. ", last_message_at: "2016-11-01 07:00:02", flavor_id: 1},
